import { useState, useEffect } from 'react';
import './App.css';
import Cards from './components/Cards/index.jsx';
import Nav from './components/Nav/index.jsx';
// import axios from "axios"; 
import About from './components/About/About.jsx'
import Detail from './components/Detail/Detail.jsx';
import { Routes, Route, useLocation, useNavigate } from 'react-router-dom';
import Form from './components/Form'

const URL = 'https://rickandmortyapi.com/api/character/'

const access = {
   email: 'prueba@gmail.com',
   password: '123456',
   isLoged: false,
 }

function App() {

const { pathname } = useLocation();
const navigate = useNavigate();

const [characters, setCharacters] = useState([]);

const onSearch = async (id) => {
try {
   const response = await fetch(`${URL}${id}`);
   const data = await response.json();
   setCharacters([...characters, data]);
   } catch (error) {
   console.log("error", error);
   }
};

      // const example = {
      //    id: 1,
      //    name: 'Rick Sanchez',
      //    status: 'Alive',
      //    species: 'Human',
      //    gender: 'Male',
      //    origin: {
      //       name: 'Earth (C-137)',
      //       url: 'https://rickandmortyapi.com/api/location/1',
      //    },
      //    image: 'https://rickandmortyapi.com/api/character/avatar/1.jpeg',
      // };

      // const onSearch = (id) => {
      //                               // ,example
      //    setCharacters([...characters])

      //    fetch (`${URL}${id}`)
      //    .then((response)=> response.json())
      //    .then((data)=>{
      //       setCharacters([...characters,data]);
      //    });
      // }

 const onClose = (id) => {
   const personajesFiltrados = characters.filter((character) => character.id !== id);
   setCharacters(personajesFiltrados);
   }

const login = (data) => {

   if (data.email === access.email && data.password === access.password) {
   access.isLoged = true;
   navigate('/home');
   } else {
   window.alert('Usuario o contraseña incorrectos');
   }
   }

const logout = () => {
    alert('adios');
    access.isLoged = false;
    navigate('/');
   }

   useEffect(() => {
   !access.isLoged && navigate('/');
   }, [navigate]);



return (
  <div className='App'>
  {pathname !== '/' && <Nav onSearch={onSearch} logout={logout} />}

  <Routes>
    {/* LOGIN */}
  <Route path='/' element={<Form loginUser={login} />} />

   {/* HOME */}
   <Route path="/home"
   element={
   <Cards characters={characters} onClose={onClose}/>}
   />

   {/* ABOUT */}
   <Route path='/about' element={<About />} />

   {/* DETAIL */}
   <Route path='/detail/:id' element={<Detail/>} />
   <Route path="*" element={<h1>404 Not Found</h1>} />

   </Routes>
   </div>
   );
};


export default App;
