import React, { useEffect, useState } from "react";
import { Validation } from "./Validation";
import styles from "../Form/index.module.css";

const Form = ({loginUser}) => {
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');

  const [userData, setData] = useState({
    email: "",
    password: "",
  });

  const [errors, setErrors] = useState({
    email: false,
    password: false,
  });

  const handleOnchange = (e) => {
    setData({
      ...userData,
      [e.target.name]: e.target.value,
    });

    setErrors (Validation({ ...userData, [e.target.name]: e.target.value }));
  };

  const handleSumit = (e) => {
    e.preventDefault();

    loginUser(userData);
  };

  return (
    <div className={styles.contenedor}>
      <form 
        onSubmit={handleSumit} className={styles.submit}>
        <Title htmlFor='email' className={styles.title}>Email</Title>
        <Input
          className={styles.input1}
          type='text'
          name='email'
          placeholder='Email'
          value={userData.email}
          // onChange={(e) => handleOnchange(e)}
          onChange={handleOnchange}
        />

        {errors.email && <p>{errors.email}</p>}

        <Title htmlFor='password' className={styles.title2}>password</Title>
        <Input
          className={styles.input2}
          type='password'
          name='password'
          placeholder='password'
          value={userData.password}
          // onChange={(e) => handleOnchange(e)}
          onChange={handleOnchange}
        />

        {errors.password && <p>{errors.password}</p>}

        <Button type='submit' className={styles.boton}>Submit</Button>
      </form>
    </div>
  );
};

export default Form;
