import React,{ useState } from 'react';
import './App.css';
import Cards from './components/Cards/index.jsx';
import Nav from './components/Nav/index.jsx';
import { Routes, Route } from 'react-router-dom';
import About from './components/About/About.jsx'
import Detail from './components/Detail/Detail.jsx';
import axios from "axios";
import Form from '../components/Form/Form.jsx';

const URL = 'https://rickandmortyapi.com/api/character/'

function App() {

   // <Form></Form>

      const [characters, setCharacters] = useState([]);
      const [ access, setAccess ] = useState(false);

      const EMAIL = 'batman@gmail.com';
      const PASSWORD = 'robin1234';

      useEffect(() => {
         !access && navigate('/');
      }, [access]);

      function login(userData) {
         if (userData.password === PASSWORD && userData.username === EMAIL) {
            setAccess(true);
            navigate('/home');
         }
      }


      const example = {
         id: 1,
         name: 'Rick Sanchez',
         status: 'Alive',
         species: 'Human',
         gender: 'Male',
         origin: {
            name: 'Earth (C-137)',
            url: 'https://rickandmortyapi.com/api/location/1',
         },
         image: 'https://rickandmortyapi.com/api/character/avatar/1.jpeg',
      };

      const onSearch = (id) => {
         setCharacters([...characters, example])

         fetch (`${URL}${id}`)
         .then((response)=> response.json())
         .then((data)=>{
            setCharacters([...characters,data]);
         });
      } 

      const onClose = (id) => {
         const personajesFiltrados = characters.filter((character) => character.id !== id);
         setCharacters(personajesFiltrados);
      }


      return (
      <div className='App'>
         <Nav onSearch={onSearch}/>
         <Routes>
            <Route path="/home"
            element={
         <Cards characters={characters} onClose={onClose}/>}
         />
         <Route path='/about' element={<About />} />
         <Route path='/detail/:id' element={<Detail/>} />
         </Routes>
      </div>
   );
}

export default App;
