        <h2 className={styles.subtitle}>{props.status}</h2>
        <h2 className={styles.subtitle}>{props.species}</h2>
        <h2 className={styles.subtitle}>{props.gender}</h2>
        <h2>{props.origin.name}</h2>
        </div>
    )
}

export default Detail;
